import React from "react";
import styled from "styled-components";

interface Iprops {
  subject: string;
}

function Cards({ subject }: Iprops) {
  const Card = styled.li`
    margin: 20px 0px;
    padding: 20px;
    font-size: 21px;
    color: #111;
    background-color: rgba(255, 255, 255, 0.7);
    box-shadow: 5px 5px 15px 3px rgba(150, 150, 150, 0.6);
    border-radius: 12px;
  `;

  const Subject = styled.h3`
    display; block;
    padding: 5px 0;
    font-size: 31px;
    line-height: 1.5;
    `;

  const ButtonWrap = styled.div`
    height: 100%;
    text-align: right;
  `;

  const ModifyBtn = styled.button`
    all: unset;
    padding: 5px 10px;
    font-size: 19px;
    color: #111;
    cursor: pointer;
  `;

  const DeleteBtn = styled.button`
    all: unset;
    padding: 5px 10px;
    font-size: 19px;
    color: #111;
    cursor: pointer;
  `;

  return (
    <>
      <Card>
        <Subject>{subject}</Subject>
        <ButtonWrap>
          <ModifyBtn type="button">수정</ModifyBtn>
          <DeleteBtn type="button">삭제</DeleteBtn>
        </ButtonWrap>
      </Card>

      <div>
        <div>
          <h3>타이틀</h3>
          <div>
            <input type="text" placeholder="" />
          </div>
          <span>담당자</span>
          <div>
            <input type="text" placeholder="" />
          </div>
          <span>날짜</span>
          <div>
            <input type="date" />
          </div>
          <span>내용</span>
          <div>
            <input type="text" placeholder="" />
          </div>
          <span>완료여부</span>
          <div>
            <input type="checkbox" />
          </div>
        </div>
      </div>
    </>
  );
}

export default Cards;
