import React from "react";
import styled from "styled-components";
import DEFAULT_DATA from "../utils/defaultdata";

function Main() {
  const Container = styled.div`
    width: 100%;
    height: 100vh;
    background-color: red;
  `;

  const Wrap = styled.section`
    display: flex;
    flex-dirction: row;
    justify-content: space-evenly;
    align-items: flex-start;
    width: 1800px;
    min-width: 1800px;
    margin: 0;
    padding: 40px;
  `;

  const Section = styled.div`
    width: 420px;
    height: auto;
    max-height: 1200px;
    min-height: 230px;
    margin: 20px auto;
    padding: 40px 0 20px;
    justify-content: space-between;
    border-radius: 12px;
    background-color: rgba(255, 255, 255, 0.8);
    box-shadow: 5px 5px 15px 3px rgba(150, 150, 150, 0.6);
    overflow: hidden;
  `;

  const Title = styled.h2`
    font-size: 41px;
    font-weight: bold;
    text-align: center;
  `;

  const List = styled.ul`
    margin: 0px 20px 10px;
    padding: 20px;
  `;

  const Card = styled.li`
    margin: 20px 0px;
    padding: 20px;
    font-size: 21px;
    color: red;
    border: 1px solid #333;
    border-radius: 12px;
  `;

  const Subject = styled.h3`
    display; block;
    padding: 5px 0;
    font-size: 31px;
    line-height: 1.5;
  `;

  const ButtonWrap = styled.div`
    height: 100%;
    text-align: right;
  `;

  const ModifyBtn = styled.button`
    all: unset;
    padding: 5px 10px;
    font-size: 19px;
    color: #111;
    cursor: pointer;
  `;

  const DeleteBtn = styled.button`
    all: unset;
    padding: 5px 10px;
    font-size: 19px;
    color: #111;
    cursor: pointer;
  `;

  const InputWrap = styled.div`
    width: 100%;
    height: 30px;
    margin-top: 30px;
  `;

  const SubjectInput = styled.input`
    width: calc(100% - 50px);
    height: 30px;
    padding: 0 10px;
    font-size: 19px;
  `;

  const SubjectAddBtn = styled.button`
    all: unset;
    width: 50px;
    font-size: 19px;
    text-align: right;
    line-height: 1.5;
  `;

  return (
    <Container>
      <Wrap>
        {DEFAULT_DATA.map(({ list_id, list_title, card_contents }) => (
          <Section key={list_id}>
            <Title>{list_title}</Title>
            <List>
              {card_contents.map(({ id, subject }) => (
                <Card key={id}>
                  <Subject>{subject}</Subject>
                  <ButtonWrap>
                    <ModifyBtn type="button">수정</ModifyBtn>
                    <DeleteBtn type="button">삭제</DeleteBtn>
                  </ButtonWrap>
                </Card>
              ))}
              <InputWrap>
                <SubjectInput type="text" placeholder="제목을 입력하세요" />
                <SubjectAddBtn type="button">추가</SubjectAddBtn>
              </InputWrap>
            </List>
          </Section>
        ))}
      </Wrap>
    </Container>
  );
}

export default Main;
